import { useState } from 'react';
import './App.css';
import TryAndError from './TryAndError'

//Star Rating 
import Rating from '@material-ui/lab/Rating';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box'
import { render } from '@testing-library/react';

function App() {

  // const [wine, setWine] = useState({});
  const [startTasting, setStartTasting] = useState(false);
  const [winery, setWinery] = useState('');
  const [year, setYear] = useState('');
  const [grape, setGrape] = useState('');
  const [error, setError] = useState(false);
  const [value, setValue] = useState(0);
  const [fruit, setFruit] = useState(0);
  const [tannins, setTannins] = useState(0);
  const [acidity, setAcidity] = useState(0);

  function handleSubmit(event) {
    event.preventDefault();
    if (winery && year && grape) {
      // const newWine = { winery: winery, year: year, grape: grape, value: 0 }
      // setWine(newWine);
      setWinery(winery);
      setYear(year);
      setGrape(grape);
      setStartTasting(true)
    } else setError(true)
  }

  function handleChangeWinery(event) {
    if (error) setError(false);
    console.log(event.target.value)
    setWinery(event.target.value);
  }

  function handleChangeYear(event) {
    if (error) setError(false);
    setYear(event.target.value);
  }

  function handleChangeGrape(event) {
    if (error) setError(false);
    setGrape(event.target.value)
  }

  function shouldShowWineRating () {

    return (startTasting === true && value === 0)
  }


  return (
    <div className='centered__container'>
    {console.log("render happening")}
    <div className='form__container'>
    {startTasting === false ?  <form onSubmit={handleSubmit}>
        <input type='text' value={winery} onChange={handleChangeWinery} placeholder='Type in name of winery ...'></input>
        <input type='text' value={year} onChange={handleChangeYear} placeholder='Type in year ...'></input>
        <input type='text' value={grape} onChange={handleChangeGrape} placeholder='Type in grape variety ...'></input>
        <button type='submit'>start tasting</button>
      </form> : (<></>)}
    </div>

      {/* Overall Rating */}
      {shouldShowWineRating() ? (<div className='star__rating'>
          <Box component="fieldset" mb={3} borderColor="transparent" className='star__box'>
            <Typography component="legend">Rate the Wine</Typography>
            <Rating
              name="overall-ranking"
              precision={0.5}
              value={value}
              onChange={(event, newValue) => {
                setValue(newValue);
              }}
            />
          </Box>
      </div>) : (<></>)}

      {(value === 0) ? (<></>) : (fruit === 0) ? (<div className='star__rating'>
        <Box component="fieldset" mb={3} borderColor="transparent" className='star__box'>
          <Typography component="legend">Rate the fruit character of the wine</Typography>
          <Rating
              name="fruit"
              precision={0.5}
              value={fruit}
              onChange={(event, newValue) => {
                setFruit(newValue);
              }}
            />
        </Box>
      </div>) : (<></>)}

      {(fruit === 0) ? (<></>) : (tannins === 0) ? (<div className='star__rating'>
        <Box component="fieldset" mb={3} borderColor="transparent" className='star__box'>
          <Typography component="legend">Rate the amount of tannins in the Wine</Typography>
          <Rating
              name="tannins"
              precision={0.5}
              value={tannins}
              onChange={(event, newValue) => {
                setTannins(newValue);
              }}
            />
        </Box>
      </div>) : (<></>)}

      {(tannins === 0) ? (<></>) : (acidity === 0) ? (<div className='star__rating'>
        <Box component="fieldset" mb={3} borderColor="transparent" className='star__box'>
          <Typography component="legend">Rate the acidity of the Wine</Typography>
          <Rating
              name="acidity"
              precision={0.5}
              value={acidity}
              onChange={(event, newValue) => {
                setAcidity(newValue);
              }}
            />
        </Box>
      </div>) : (<></>)}

    {acidity !== 0 ?  <button className='showDataButtonn' onClick={() => console.log("winery:", winery, "grape", grape, "year", year, "Ratinng", value, "fruis",fruit, "tannins",tannins, "acidity",acidity)}>show wine list</button> : (<></>)}



    {/* {lucas === "tonto" ?  <TryAndError /> : <div></div>} */}


    </div>
  );
}

export default App;
