import "./App.css";
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

import WineTasting from "./Components/WineTasting/WineTasting";
import WineList from "./Components/WineList/WineList"

export default function App() {

  const [wineList, setWineList] = useState([])

  return (
    <Router>
      <div className="grid__container">
        <nav className="navbar">
        <div className='underline__navbar'>
            <Link to="/" className='link__navbar__header' wineList={wineList}>Virtual Wine Tasting</Link>
          </div>
          <div className='underline__navbar'>
            <Link to="/" className='link__navbar'>Home</Link>
          </div>
          <div className='underline__navbar'>
            <Link to="/tasting" className='link__navbar'>Start Wine Tasting</Link>
          </div>
          <div className='underline__navbar'>
            <Link to="/winelist" className='link__navbar'>Your Personal Wine List</Link>
          </div>
          <div className='underline__navbar'>
            <Link to="/user" className='link__navbar'>User Profile</Link>
          </div>
        </nav>

        {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}

        <div className="main">
          <Switch>
            <Route path="/tasting">
              <WineTasting />
            </Route>
            <Route path="/winelist"><WineList /></Route>
            <Route path="/user">{/* <Users /> */}</Route>
            <Route path="/">{/* <Home /> */}</Route>
          </Switch>
        </div>
      </div>
    </Router>
  );
}

