import { useState } from 'react';

export default function DominantFlavors({ flavors, grape, wineDB }) {

  const [fruitFlavors, setFruitFlavors] = useState({})
  const [dryFruitFlavors, setDryFruitFlavors] = useState({})
  const [floralFlavors, setFloralFlavors] = useState({})
  const [herbalFlavors, setHerbalFlavors] = useState({})
  const [spiceFlavors, setSpiceFlavors] = useState({})
  const [earthFlavors, setEarthFlavors] = useState({})
  const [otherFlavors, setOtherFlavors] = useState({})

  function updateFruitFlavors(fruit) {
    if (fruitFlavors[fruit] === fruit) {
      delete fruitFlavors[fruit]
      setFruitFlavors(prev => ({
        ...prev
      }))
    } else {
      setFruitFlavors(prev => ({
        ...prev,
        [fruit]: fruit
      }))
    }
  }

  function updateDryFruitFlavors(dryFruit) {
    if (dryFruitFlavors[dryFruit] === dryFruit) {
      delete dryFruitFlavors[dryFruit]
      setDryFruitFlavors(prev => ({
        ...prev
      }))
    } else {
      setDryFruitFlavors(prev => ({
        ...prev,
        [dryFruit]: dryFruit
      }))
    }
  }

  function updateFloralFlavors(floral) {
    if (floralFlavors[floral] === floral) {
      delete floralFlavors[floral]
      setFloralFlavors(prev => ({
        ...prev
      }))
    } else {
      setFloralFlavors(prev => ({
        ...prev,
        [floral]: floral
      }))
    }
  }

  function updateHerbalFlavors(herbal) {
    if (herbalFlavors[herbal] === herbal) {
      delete herbalFlavors[herbal]
      setHerbalFlavors(prev => ({
        ...prev
      }))
    } else {
      setHerbalFlavors(prev => ({
        ...prev,
        [herbal]: herbal
      }))
    }
  }

  function updateSpiceFlavors(spice) {
    if (spiceFlavors[spice] === spice) {
      delete spiceFlavors[spice]
      setSpiceFlavors(prev => ({
        ...prev
      }))
    } else {
      setSpiceFlavors(prev => ({
        ...prev,
        [spice]: spice
      }))
    }
  }

  function updateEarthFlavors(earth) {
    if (earthFlavors[earth] === earth) {
      delete earthFlavors[earth]
      setEarthFlavors(prev => ({
        ...prev
      }))
    } else {
      setEarthFlavors(prev => ({
        ...prev,
        [earth]: earth
      }))
    }
  }

  function updateOtherFlavors(other) {
    if (otherFlavors[other] === other) {
      delete otherFlavors[other]
      setOtherFlavors(prev => ({
        ...prev
      }))
    } else {
      setOtherFlavors(prev => ({
        ...prev,
        [other]: other
      }))
    }
  }

  return (<div>

    <span className='possible__flavor__headline'><h2>   possible flavors in {grape} </h2></span>

    <h3>Fruits Flavors</h3>
    <div className='possible__flavors'>{wineDB[grape].possibleFlavors.fruits.map((fruit) => <div
      onClick={() => updateFruitFlavors(fruit)}
      className={(fruitFlavors[fruit] === fruit ? 'toggled__flavor' : '') + ' flavor__box'} >{fruit}</div>)}
    </div>

    <h3>Dry Fruits Flavors</h3>
    <div className='possible__flavors'>{wineDB[grape].possibleFlavors.dryFruits.map((dryFruit) => <div
      onClick={() => updateDryFruitFlavors(dryFruit)}
      className={(dryFruitFlavors[dryFruit] === dryFruit ? 'toggled__flavor' : '') + ' flavor__box'}>{dryFruit}</div>)}
    </div>

    <h3>Flora Flavors:</h3>
    <div className='possible__flavors'>{wineDB[grape].possibleFlavors.florals.map((floral) => <div
      onClick={() => updateFloralFlavors(floral)}
      className={(floralFlavors[floral] === floral ? 'toggled__flavor' : '') + ' flavor__box'}>{floral}</div>)}
    </div>

    <h3>Herbal Flavors</h3>
    <div className='possible__flavors'>{wineDB[grape].possibleFlavors.herbs.map((herb) => <div
      onClick={() => updateHerbalFlavors(herb)}
      className={(herbalFlavors[herb] === herb ? 'toggled__flavor' : '') + ' flavor__box'}>{herb}</div>)}
    </div>

    <h3>Spice Flavors:</h3>
    <div className='possible__flavors'>{wineDB[grape].possibleFlavors.spices.map((spice) => <div
      onClick={() => updateSpiceFlavors(spice)}
      className={(spiceFlavors[spice] === spice ? 'toggled__flavor' : '') + ' flavor__box'}>{spice}</div>)}
    </div>

    <h3>Earthy Flavors</h3>
    <div className='possible__flavors'>{wineDB[grape].possibleFlavors.earthFlavors.map((earthFlavor) => <div
      onClick={() => updateEarthFlavors(earthFlavor)}
      className={(earthFlavors[earthFlavor] === earthFlavor ? 'toggled__flavor' : '') + ' flavor__box'}>{earthFlavor}</div>)}
    </div>

    <h3>Other Flavors</h3>
    <div className='possible__flavors'>{wineDB[grape].possibleFlavors.others.map((other) => <div
      onClick={() => updateOtherFlavors(other)}
      className={(otherFlavors[other] === other ? 'toggled__flavor' : '') + ' flavor__box'}>{other}</div>)}
    </div>

    <button onClick={() => console.log(fruitFlavors)}>show fruit flavors</button>
  </div>

  );
}
