import { useState } from 'react';
import { withStyles } from '@material-ui/core/styles';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';

const BlackCheckbox = withStyles({
  root: {
    color: 'black',
    '&$checked': {
      color: 'black',
    },
  },
  checked: {},
})((props) => <Checkbox color="default" {...props} />);

export default function DominantFlavors({ flavors, grape, wineDB }) {

  const [state, setState] = useState({
    checkedA: true,
    checkedB: true,
    checkedC: false,
    checkedD: true,
  });

  const handleChange = (event) => {
    console.log(event.target.defaultValue)
    setState({ ...state, [event.target.name]: event.target.checked });
  };

  return (<div>
    <FormGroup row>

      <FormControlLabel
        control={
          <BlackCheckbox
            value={'Vanilla'}
            checked={state.checkedC}
            onChange={handleChange}
            name="checkedC" />}
        label="Vanilla"
      />
    </FormGroup>
    <div><h2>possible flavors</h2></div>
    <div>Fruits: {wineDB[grape].possibleFlavors.fruits.map((fruit) => <div>{fruit}</div>)}</div>
    <div>Dry Fruits: {wineDB[grape].possibleFlavors.dryFruits.map((dryFruit) => <div>{dryFruit}</div>)}</div>
    <div>Floral: {wineDB[grape].possibleFlavors.florals.map((floral) => <div>{floral}</div>)}</div>
    <div>Herbs: {wineDB[grape].possibleFlavors.herbs.map((herb) => <div>{herb}</div>)}</div>
    <div>Spices: {wineDB[grape].possibleFlavors.spices.map((spice) => <div>{spice}</div>)}</div>
    <div>Earth: {wineDB[grape].possibleFlavors.earthFlavors.map((earthFlavor) => <div>{earthFlavor}</div>)}</div>
    <div>Other: {wineDB[grape].possibleFlavors.others.map((other) => <div>{other}</div>)}</div>

    <button onClick={() => console.log(wineDB[grape].possibleFlavors.fruits)}>show state</button>
  </div>

  );
}
