import { useState } from 'react';
import './App.css';

//Star Rating 
import Rating from '@material-ui/lab/Rating';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box'

function App() {

  const [wine, setWine] = useState([]);
  const [winery, setWinery] = useState('');
  const [year, setYear] = useState('');
  const [grape, setGrape] = useState('');
  const [error, setError] = useState(false);
  const [value, setValue] = useState(0);

  function handleSubmit(event) {
    event.preventDefault();
    if (winery && year && grape) {
      setWine({ winery: winery, year: year, grape: grape })
      setWinery('');
      setYear('');
      setGrape('')
      console.log(wine)
    } else setError(true)
  }

  function handleChangeWinery(event) {
    if (error) setError(false);
    console.log(event.target.value)
    setWinery(event.target.value);
  }

  function handleChangeYear(event) {
    if (error) setError(false);
    setYear(event.target.value);
  }

  function handleChangeGrape(event) {
    if (error) setError(false);
    setGrape(event.target.value)
  }




  return (
    <div>

      <form onSubmit={handleSubmit}>
        <input type='text' value={winery} onChange={handleChangeWinery} placeholder='Type in name of winery ...'></input>
        <input type='text' value={year} onChange={handleChangeYear} placeholder='Type in year ...'></input>
        <input type='text' value={grape} onChange={handleChangeGrape} placeholder='Type in grape variety ...'></input>
        <button type='submit'>start tasting</button>
      </form>

      <button onClick={() => console.log(wine)}>show wine list</button>
      <div>{wine.winery} {wine.year} {wine.grape}</div>

      {wine.grape === 'malbec' ? (<div>
        <div>
          <Box component="fieldset" mb={3} borderColor="transparent">
            <Typography component="legend">Rate the Wine</Typography>
            <Rating
              name="simple-controlled"
              value={value}
              onChange={(event, newValue) => {
                setValue(newValue);

              }}
            />
          </Box>
        </div>
      </div>) : (<div></div>)}

    </div>
  );
}

export default App;
