import './App.css';
import { useState } from 'react';
import FruitRating from './FruitRating';
import BodyRating from './BodyRating';
import TanninsRating from './TanninsRating';
import AcidityRating from './AcidityRating';


function App() {

  const [startTasting, setStartTasting] = useState(false);
  const [winery, setWinery] = useState('');
  const [year, setYear] = useState('');
  const [grape, setGrape] = useState('');
  const [error, setError] = useState(false);
  const [body, setBody] = useState(0);
  const [fruit, setFruit] = useState(0);
  const [tannins, setTannins] = useState(0);
  const [acidity, setAcidity] = useState(0);

  function handleSubmit(event) {
    event.preventDefault();
    if (winery && year && grape) {
      setWinery(winery);
      setYear(year);
      setGrape(grape);
      setStartTasting(true);
    } else setError(true);
  }

  function handleChangeWinery(event) {
    if (error) setError(false);
    setWinery(event.target.value);
  }

  function handleChangeYear(event) {
    if (error) setError(false);
    setYear(event.target.value);
  }

  function handleChangeGrape(event) {
    if (error) setError(false);
    setGrape(event.target.value);
  }

  function showRatingOfWineBody() {
    return (startTasting === true && body === 0);
  }

  function showRatingOfFruit() {
    return (body !== 0 && fruit === 0);
  }

  function showRatingOfTannins() {
    return (fruit !== 0 && tannins === 0);
  }

  function showRatingOfAcidity() {
    return (tannins !== 0 && acidity === 0);
  }

  function updateBody(event, value) {
    setBody(value);
  }

  function updateFruit(event, value) {
    setFruit(value);
  }

  function updateTannins(event, value) {
    setTannins(value);
  }

  function updateAcidity(event, value) {
    setAcidity(value);
  }


  return (
    <div>
      <h1>virtual wine tasting</h1>
      <div className='centered__container'>
        {console.log("render happening")}
        <div className='form__container'>
          {startTasting === false ? <form onSubmit={handleSubmit}>
            <input type='text' value={winery} onChange={handleChangeWinery} placeholder='Type in name of winery ...'></input>
            <input type='text' value={year} onChange={handleChangeYear} placeholder='Type in year ...'></input>
            <input type='text' value={grape} onChange={handleChangeGrape} placeholder='Type in grape variety ...'></input>
            <button type='submit'>start tasting</button>
          </form> : (<></>)}
        </div>

        {showRatingOfWineBody() ? (<BodyRating body={body} updateBody={updateBody} />) : (<></>)}

        {showRatingOfFruit() ? (<FruitRating fruit={fruit} updateFruit={updateFruit} />) : (<></>)}

        {showRatingOfTannins() ? (<TanninsRating tannins={tannins} updateTannins={updateTannins}/>) : (<></>)}

        {showRatingOfAcidity() ? (<AcidityRating acidity={acidity} updateAcidity={updateAcidity}/>) : (<></>)}

        {acidity !== 0 ? <button className='showDataButtonn' onClick={() => console.log("winery:", winery, "grape", grape, "year", year, "body", body, "frutis", fruit, "tannins", tannins, "acidity", acidity)}>show wine list</button> : (<></>)}

      </div>
    </div>
  );
}

export default App;
