import Rating from '@material-ui/lab/Rating';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box'
import FiberManualRecordIcon from '@material-ui/icons/FiberManualRecord';
import { withStyles } from '@material-ui/core/styles';

const StyledRating = withStyles({
  iconFilled: {
    color: 'black',
  },
  iconHover: {
    color: 'black',
  },
})(Rating);

function AcidityRating({ acidity, updateAcidity }) {
  return (<div className='star__rating'>
    <Box component="fieldset" mb={3} borderColor="transparent" className='star__box'>
      <Typography component="legend">Rate the acidity of the Wine</Typography>
      <StyledRating
        name="acidity"
        precision={0.5}
        value={acidity}
        icon={<FiberManualRecordIcon fontSize="inherit" />}
        onChange={(event, newValue) => updateAcidity(event, newValue)}
      />
    </Box>
    <div className='profile__tasting__description__text'>To taste tannin in wine, focus on the texture on your toungue. A high tannin wine will remove proteins from your tongue, causing a drying and puckering sensation. This sensation is often described as 'grippy'. High tannin wines act as palate cleanser to rich, fatty meats, cheeses and pasta dishes.
    </div>
  </div>)
}

export default AcidityRating
