import { useState } from "react"

export default function WineCard ({wine}) {

  const [dominantFlavors, setDominantFlavors] = useState([])

  for(let key of Object.keys(wine.dominantFlavors.flavors)) {
    //setDominantFlavors(prev => [...prev, key])
    dominantFlavors.push(key)
  }


  return (
    <div>
    <div className='wine__card__container'>
      <div>Winery: {wine.winery}</div>
      <div>Year: {wine.year}</div>
      <div>Grape: {wine.grape}</div>
      <div>Fruit: {wine.fruit}</div>
      <div>Acidity: {wine.acidity}</div>
      <div>Tannins: {wine.tannins}</div>
      <div>Body: {wine.body}</div>
      <div>Dominant Flavors: {dominantFlavors}</div>
      {/* <div>Dominant Flavors: {dominantFlavors.map(domFlav =><p>{domFlav}</p>)}</div> */}
      {/*
      <div>{wine.possibleFlavors}</div>*/}
      <div>{wine.overallRating}</div>
      <button onClick={() => console.log(dominantFlavors)}>console log</button>
    </div>


    </div>
  )
}
