import { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCheck } from '@fortawesome/free-solid-svg-icons'

export default function Login({ loginUser }) {

  const [mail, setMail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(false);
  const [userExists, setUserExists] = useState(true);
  const [successfullyRegistrated, setSuccessfullyRegistrated] = useState(false);

  function handleSubmit(event) {
    event.preventDefault();
    if (mail && password) {
      setMail(mail);
      setPassword(password);
    } else setError(true);
  }

  function handleChangeMail(event) {
    if (error) setError(false);
    setMail(event.target.value);
  }

  function handleChangePassword(event) {
    if (error) setError(false);
    setPassword(event.target.value);
  }

  function registerUser() {
    setTimeout(function () { setUserExists(true); }, 1300);
  }

  return (<div>

    {userExists === true ? (<div className="centered__container__login">
      <div className='login__headline'><h2>User Login</h2></div>
      <div className="login__form">
        <form onSubmit={handleSubmit}>
          <input
            className='input__login'
            type="text"
            value={mail}
            onChange={handleChangeMail}
            placeholder="Type in your user name ..."
          ></input>
          <input
            className='input__login'
            type="text"
            value={password}
            onChange={handleChangePassword}
            placeholder="Type in your password ..."
          ></input>
          <button type="submit" onClick={() => loginUser(mail, password)}>login</button>
          <div>to create a new user click <div onClick={() => setUserExists(false)}>here</div></div>
        </form>
      </div>
    </div>) : successfullyRegistrated === false ? (<div className="centered__container__login">
      <div className='login__headline'><h2>Register new User</h2></div>
      <div className="login__form">
        <form onSubmit={handleSubmit}>
          <input
            className='input__login'
            type="text"
            value={mail}
            onChange={handleChangeMail}
            placeholder="Type in your user name ..."
          ></input>
          <input
            className='input__login'
            type="text"
            value={password}
            onChange={handleChangePassword}
            placeholder="Type in your password ..."
          ></input>
          <button type="submit" onClick={() => registerUser()}>register</button>
        </form>
      </div>
    </div>) : (<FontAwesomeIcon icon={faCheck} />
    )}

  </div>
  )

}
