import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Rating from '@material-ui/lab/Rating';
import SentimentVeryDissatisfiedIcon from '@material-ui/icons/SentimentVeryDissatisfied';
import SentimentDissatisfiedIcon from '@material-ui/icons/SentimentDissatisfied';
import SentimentSatisfiedIcon from '@material-ui/icons/SentimentSatisfied';
import SentimentSatisfiedAltIcon from '@material-ui/icons/SentimentSatisfiedAltOutlined';
import SentimentVerySatisfiedIcon from '@material-ui/icons/SentimentVerySatisfied';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import FiberManualRecordIcon from '@material-ui/icons/FiberManualRecord';
import FavoriteIcon from '@material-ui/icons/Favorite';


const StyledRating = withStyles({
  iconFilled: {
    color: '#ff6d75',
  },
  iconHover: {
    color: '#ff3d47',
  },      
})(Rating);

const customIcons = {
  1: {
    icon: <SentimentVeryDissatisfiedIcon />,
    label: 'Very Dissatisfied',
  },
  2: {
    icon: <SentimentDissatisfiedIcon />,
    label: 'Dissatisfied',
  },
  3: {
    icon: <SentimentSatisfiedIcon />,
    label: 'Neutral',
  },
  4: {
    icon: <SentimentSatisfiedAltIcon />,
    label: 'Satisfied',
  },
  5: {
    icon: <SentimentVerySatisfiedIcon />,
    label: 'Very Satisfied',
  },
};



export default function OverallRating({updateOverallRating}) {

  const [value, setValue] = useState(0);

  function IconContainer(props) {
    const { value, ...other } = props;
    setValue(value)
    return <span {...other}>{customIcons[value].icon}</span>;
  }

  IconContainer.propTypes = {
    value: PropTypes.number.isRequired,
  };

  return (
    <div>
      <h2 className='rating__box__headline'>overall rating of the wine</h2>

      <div classNam='asdfghjk'>
        <div className='rating__box__headline__subheadline'><h2>Conclude</h2></div>
        <div className='final__evaluation__text'>
          <div>
            Now that you have tasted the wine, you can evaluate it. Do all the traits in the wine balance one another? A wine that is out of balance will have characteristics that overpower other flavors in the wine, for example a jarring acidic flavor that dominates the taste. Take your time with wine that you enjoy. Identify what you prefer about them over other wines. You will find yourself to be more articulate when seeking new wine. We use a simple 5-point rating system with focus on drinkability
          </div>
        </div>

        <div>
          {/* <Box component="fieldset" mb={3} borderColor="transparent" className='overall__rating__box'>
            <Typography component="legend" className='typography__header'>personal rating</Typography>
            <Rating
              name="customized-icons"
              defaultValue={2}
              getLabelText={(value) => customIcons[value].label}
              IconContainerComponent={IconContainer}
            />
          </Box> */}
          <Box component="fieldset" mb={3} borderColor="transparent" className='rating__box'>
        <Typography component="legend"></Typography>
        <StyledRating
          className="style__rating"
          name="fruit"
          precision={0.5}
          value={value}
          icon={<FavoriteIcon fontSize="inherit" />}
          // icon={<FiberManualRecordIcon fontSize="inherit" />}
          onClick={(event, newValue) => setValue(newValue)}
        />
      </Box> 
        </div>

        <div>
          {/* <div>
            <input type='text' className='textfield__personal__notes' placeholder='anything else? you can add some personal notes here ...'></input>
          </div> */}
          <div>
            <button className='add__wine__button' onClick={() => updateOverallRating()}>Add wine to your wine list</button>
            <button className='add__wine__button' onClick={() => console.log(value)}>vlaue</button>
          </div>
        </div>

        {/* <input type='text'>custom comment</input> */}
      </div>
    </div>
  );
}
