import { useState } from "react"

export default function WineCard ({wine}) {

  //const [dominantFlavors, setDominantFlavors] = useState([])
  const [possibleFlavors, setPossibleFlavors] = useState([])

  const dominantFlavors = []

  for(let key of Object.keys(wine.dominantFlavors.flavors)) {
    //setDominantFlavors(prev => [...prev, key])
    dominantFlavors.push(key)

  }

  for(let key of Object.keys(wine.possibleFlavors)) {
    if (wine.possibleFlavors.hasOwnProperty(key)) { 
      possibleFlavors.push(wine.possibleFlavors[key])
    }
  }

  // function renderPossibleFlavors() {
  //   for(let key of Object.keys(wine.dominantFlavors.flavors)) {
  //     <div>key</div>
  //   }
  // }


  return (
    <div>
    <div className='wine__card__container'>
      <div>Winery: {wine.winery}</div>
      <div>Year: {wine.year}</div>
      <div>Grape: {wine.grape}</div>
      <div>Fruit: {wine.fruit}</div>
      <div>Acidity: {wine.acidity}</div>
      <div>Tannins: {wine.tannins}</div>
      <div>Body: {wine.body}</div> 
      <div>Dominant Flavors: {dominantFlavors.map(flavor => <div>{flavor}</div>)}</div>
      {/* <div>PossibleFlavors Flavors: {dominantFlavors.map(flavor => <div>{flavor}</div>)}</div> */}
      {/* <div>Dominant Flavors: {dominantFlavors.map(domFlav =><p>{domFlav}</p>)}</div> */}
      {/*
      <div>{wine.possibleFlavors}</div>*/}
      <div>Overall Rating: {wine.overallRating}</div>
      <button onClick={() => console.log(possibleFlavors)}>console log</button>
    </div>


    </div>
  )
}
