import "./App.css";
import { useState } from "react";
import FruitRating from "./Components/wineProfile/FruitRating";
import BodyRating from "./Components/wineProfile/BodyRating";
import TanninsRating from "./Components/wineProfile/TanninsRating";
import AcidityRating from "./Components/wineProfile/AcidityRating";
import PossibleFlavors from "./Components/wineFlavors/PossibleFlavors";
import WineDB from "./Components/wineDB/WineDB";
import OverallRating from "./OverallRating";
import DominantFlavors from "./Components/wineFlavors/DominantFlavors";

function App() {
  const [startTasting, setStartTasting] = useState(false);
  const [winery, setWinery] = useState("");
  const [year, setYear] = useState("");
  const [grape, setGrape] = useState("");
  const [error, setError] = useState(false);
  const [body, setBody] = useState(0);
  const [fruit, setFruit] = useState(0);
  const [tannins, setTannins] = useState(0);
  const [acidity, setAcidity] = useState(0);
  const [possibleFlavors, setPossibleFlavors] = useState({});
  const [dominantFlavors, setDominantFlavors] = useState([]);
  const [overallRating, setOverallRating] = useState(0);
  const [wineList, setWineList] = useState([])

  function handleSubmit(event) {
    event.preventDefault();
    if (winery && year && grape) {
      setWinery(winery);
      setYear(year);
      setGrape(grape);
      setStartTasting(true);
    } else setError(true);
  }

  function handleChangeWinery(event) {
    if (error) setError(false);
    setWinery(event.target.value);
  }

  function handleChangeYear(event) {
    if (error) setError(false);
    setYear(event.target.value);
  }

  function handleChangeGrape(event) {
    if (error) setError(false);
    setGrape(event.target.value);
  }

  function showRatingOfFruit() {
    return startTasting === true && fruit === 0;
  }

  function showRatingOfAcidity() {
    return fruit !== 0 && acidity === 0;
  }

  function showRatingOfTannins() {
    return acidity !== 0 && tannins === 0;
  }

  function showRatingOfWineBody() {
    return tannins !== 0 && body === 0;
  }

  function updateBody(event, value) {
    setBody(value);
  }

  function updateFruit(event, value) {
    setFruit(value);
  }

  function updateTannins(event, value) {
    setTannins(value);
  }

  function updateAcidity(event, value) {
    setAcidity(value);
  }

  function updateDominantFlavors(flavors) {
    setDominantFlavors(flavors);
  }

  function updatePossibleFlavors(flavors) {
    setPossibleFlavors(flavors);
  }

  function updateOverallRating(value) {
    setOverallRating(value);
  }

  return (
    <div>
      {/* {console.log(WineDB)} */}
      <h1>virtual wine tasting</h1>
      <div className="centered__container">
        {console.log("render happening")}
        <div className="form__container">
          {startTasting === false ? (
            <form onSubmit={handleSubmit}>
              <input
                type="text"
                value={winery}
                onChange={handleChangeWinery}
                placeholder="Type in name of winery ..."
              ></input>
              <input
                type="text"
                value={year}
                onChange={handleChangeYear}
                placeholder="Type in year ..."
              ></input>
              <input
                type="text"
                value={grape}
                onChange={handleChangeGrape}
                placeholder="Type in grape variety ..."
              ></input>
              <button type="submit">start tasting</button>
            </form>
          ) : (
            <></>
          )}
        </div>

        {showRatingOfFruit() ? (
          <FruitRating fruit={fruit} updateFruit={updateFruit} />
        ) : (
          <></>
        )}

        {showRatingOfAcidity() ? (
          <AcidityRating acidity={acidity} updateAcidity={updateAcidity} />
        ) : (
          <></>
        )}

        {showRatingOfTannins() ? (
          <TanninsRating tannins={tannins} updateTannins={updateTannins} />
        ) : (
          <></>
        )}

        {showRatingOfWineBody() ? (
          <BodyRating body={body} updateBody={updateBody} />
        ) : (
          <></>
        )}

        {body !== 0 && !dominantFlavors.ratingCompleted ? (
          <DominantFlavors
            updateDominantFlavors={updateDominantFlavors}
            grape={grape}
            wineDB={WineDB}
          />
        ) : (
          <></>
        )}

        {dominantFlavors.ratingCompleted && !possibleFlavors.ratingCompleted ? (
          <PossibleFlavors
            updatePossibleFlavors={updatePossibleFlavors}
            grape={grape}
            wineDB={WineDB}
          />
        ) : (
          <></>
        )}

        {possibleFlavors.ratingCompleted === true &&
        !overallRating.ratingCompleted ? (
          <OverallRating updateOverallRating={updateOverallRating} />
        ) : (
          <></>
        )}

        {overallRating.ratingCompleted === true ? (
          <button
            className="show__all__wines"
            onClick={() =>
              console.log(
                "winery",
                winery,
                "grape",
                grape,
                "year",
                year,
                "fruit",
                fruit,
                "acidity",
                acidity,
                "tannins",
                tannins,
                "dominantFlavors",
                dominantFlavors,
                "possibleFlavors",
                possibleFlavors,
                "overallRating",
                overallRating
              )
            }
          >
            Show the rated wine
          </button>
        ) : (
          <></>
        )}
      </div>
    </div>
  );
}

export default App;
