const axios = require('axios');
const url = 'http://localhost:3001';

// GET ALL WINE TASTINGS FROM DB
async function getTastings() {
  try {
    const response = await axios.get(url + '/api/tastings');
    // console.log(response);
    return response.data;
  } catch (error) {
    console.error(error);
  }
}

export default { getTastings };


