import "./App.css";
import React, { useState } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import WineTasting from "./Components/WineTasting/WineTasting";
import WineList from "./Components/WineList/WineList"
import Login from "./Components/Login/Login"

export default function App() {

  // old state for locally stored data
  // const [wineList, setWineList] = useState([])
  // function updateWineList(list) {
  //   setWineList(prev => [...prev, list])
  // }

  const [user, setUser] = useState({ mail: '', password: '' });

  function loginUser (mail, password) {
    setUser({mail: mail, password: password});
  }

  return (
    <Router>
      {(user.password === 1234 && user.mail === 'maxdraguhn@gmx.net') ? (<div className="grid__container">
        <nav className="navbar sticky">
          <div className='underline__navbar'>
            <Link to="/" className='link__navbar__header'>Virtual Wine Tasting</Link>
            {/* old route for locally stored data */}
            {/* <Link to="/" className='link__navbar__header' updateWineList={updateWineList}>Virtual Wine Tasting</Link> */}
          </div>
          <button onClick={()=> console.log(user)}>show user</button>
          <div className='underline__navbar'>
            <Link to="/" className='link__navbar'>Home</Link>
          </div>
          <div className='underline__navbar'>
            <Link to="/tasting" className='link__navbar'>Start Wine Tasting</Link>
          </div>
          <div className='underline__navbar'>
            <Link to="/winelist" className='link__navbar'>Your Personal Wine List</Link>
          </div>
          <div className='underline__navbar'>
            <Link to="/login" className='link__navbar'>login</Link>
          </div>
        </nav>
        <div className="main">
          <Switch>
            <Route path="/tasting">
              <WineTasting />
              {/* old route for locally stored data */}
              {/* <WineTasting updateWineList={updateWineList} /> */}
            </Route>
            <Route path="/winelist"><WineList /></Route>
            {/* old route for locally stored data */}
            {/* <Route path="/winelist"><WineList wineList={wineList} /></Route> */}
            <Route path="/login">{/* <Users /> */}</Route>
            <Route path="/">{/* <Home /> */}</Route>
          </Switch>
        </div>
      </div>) : (<Login loginUser={loginUser}/>)}
    </Router>
  );
}
