import { useState } from 'react';

export default function DominantFlavors({ flavors, grape, wineDB }) {

  const [fruitFlavors, setFruitFlavors] = useState({})
  const [dryFruitFlavors, setDryFruitFlavors] = useState([])
  const [floralFlavors, setFloralFlavors] = useState([])
  const [herbalFlavors, setHerbalFlavors] = useState([])
  const [spiceFlavors, setSpiceFlavors] = useState([])
  const [earthFlavors, setEarthFlavors] = useState([])
  const [otherFlavors, setOtherFlavors] = useState([])

  function toggleFlavor () {
    console.log("toggle")
    console.log(fruitFlavors)
    return 'toggled__flavor'
  }

  function untoggleFlavor () {
    console.log("untoggle")
    console.log(fruitFlavors)

    return 'untoggled__flavor'
  }

  function updateFruitFlavors(fruit) {
    if (fruitFlavors[fruit] === fruit) {
      delete fruitFlavors[fruit];
    } else {
      setFruitFlavors(prev => ({
        ...prev,
        [fruit]: fruit
      }))
    }
  }

  function updateDryFruitFlavors(dryFruit) {
    setDryFruitFlavors(prev => [...prev, dryFruit])
  }

  function updateFloralFlavors(floral) {
    setFloralFlavors(prev => [...prev, floral])
  }

  function updateHerbalFlavors(herb) {
    setHerbalFlavors(prev => [...prev, herb])
  }

  function updateSpiceFlavors(spice) {
    setSpiceFlavors(prev => [...prev, spice])
  }

  function updateEarthFlavors(earthFlavor) {
    setEarthFlavors(prev => [...prev, earthFlavor])
  }

  function updateOtherFlavors(other) {
    setOtherFlavors(prev => [...prev, other])
  }

  return (<div>

    <div><h2>possible flavors</h2></div>

    <div>Fruits: {wineDB[grape].possibleFlavors.fruits.map((fruit) => <div onClick={() => updateFruitFlavors(fruit)} className={fruitFlavors}>{fruit}</div>)}</div>

    <div>Dry Fruits: {wineDB[grape].possibleFlavors.dryFruits.map((dryFruit) => <div onClick={() => updateDryFruitFlavors(dryFruit)}>{dryFruit}</div>)}</div>

    <div>Floral: {wineDB[grape].possibleFlavors.florals.map((floral) => <div onClick={() => updateFloralFlavors(floral)}>{floral}</div>)}</div>

    <div>Herbs: {wineDB[grape].possibleFlavors.herbs.map((herb) => <div onClick={() => updateHerbalFlavors(herb)}>{herb}</div>)}</div>

    <div>Spices: {wineDB[grape].possibleFlavors.spices.map((spice) => <div onClick={() => updateSpiceFlavors(spice)}>{spice}</div>)}</div>

    <div>Earth: {wineDB[grape].possibleFlavors.earthFlavors.map((earthFlavor) => <div onClick={() => updateEarthFlavors(earthFlavor)}>{earthFlavor}</div>)}</div>

    <div>Other: {wineDB[grape].possibleFlavors.others.map((other) => <div onClick={() => updateOtherFlavors(other)}>{other}</div>)}</div>

    <button onClick={() => console.log(fruitFlavors)}>show fruit flavors</button>
  </div>

  );
}
