import { useState } from 'react';

export default function DominantFlavors({ flavors, grape, wineDB }) {

  const [fruitFlavors, setFruitFlavors] = useState({})
  const [dryFruitFlavors, setDryFruitFlavors] = useState({})
  const [floralFlavors, setFloralFlavors] = useState({})
  const [herbalFlavors, setHerbalFlavors] = useState({})
  const [spiceFlavors, setSpiceFlavors] = useState({})
  const [earthFlavors, setEarthFlavors] = useState({})
  const [otherFlavors, setOtherFlavors] = useState({})

  function updateFruitFlavors(fruit) {
    if (fruitFlavors[fruit] === fruit) {
      delete fruitFlavors[fruit]
      setFruitFlavors(prev => ({
        ...prev
      }))
    } else {
      setFruitFlavors(prev => ({
        ...prev,
        [fruit]: fruit
      }))
    }
  }

  // function updateDryFruitFlavors(dryFruit) {
  //   setDryFruitFlavors(prev => [...prev, dryFruit])
  // }

  function updateFruitFlavors(dryFruit) {
    if (dryFruitFlavors[dryFruit] === dryFruit) {
      delete dryFruitFlavors[dryFruit]
      setDryFruitFlavors(prev => ({
        ...prev
      }))
    } else {
      setDryFruitFlavors(prev => ({
        ...prev,
        [dryFruit]: dryFruit
      }))
    }
  }

  function updateFloralFlavors(floral) {
    setFloralFlavors(prev => [...prev, floral])
  }

  function updateHerbalFlavors(herb) {
    setHerbalFlavors(prev => [...prev, herb])
  }

  function updateSpiceFlavors(spice) {
    setSpiceFlavors(prev => [...prev, spice])
  }

  function updateEarthFlavors(earthFlavor) {
    setEarthFlavors(prev => [...prev, earthFlavor])
  }

  function updateOtherFlavors(other) {
    setOtherFlavors(prev => [...prev, other])
  }

  return (<div>

    <div><h2>possible flavors</h2></div>

    <div>Fruits: {wineDB[grape].possibleFlavors.fruits.map((fruit) => <div onClick={() => updateFruitFlavors(fruit)} className={fruitFlavors[fruit] === fruit ? 'toggled__flavor' : ''}>{fruit}</div>)}</div>

    <div>Dry Fruits: {wineDB[grape].possibleFlavors.dryFruits.map((dryFruit) => <div onClick={() => updateDryFruitFlavors(dryFruit)} className={fruitFlavors[dryFruit] === dryFruit ? 'toggled__flavor' : ''}>{dryFruit}</div>)}</div>

    <div>Floral: {wineDB[grape].possibleFlavors.florals.map((floral) => <div onClick={() => updateFloralFlavors(floral)} className={fruitFlavors[floral] === floral ? 'toggled__flavor' : ''}>{floral}</div>)}</div>

    <div>Herbs: {wineDB[grape].possibleFlavors.herbs.map((herb) => <div onClick={() => updateHerbalFlavors(herb)} className={fruitFlavors[herb] === herb ? 'toggled__flavor' : ''}>{herb}</div>)}</div>

    <div>Spices: {wineDB[grape].possibleFlavors.spices.map((spice) => <div onClick={() => updateSpiceFlavors(spice)} className={fruitFlavors[spice] === spice ? 'toggled__flavor' : ''}>{spice}</div>)}</div>

    <div>Earth: {wineDB[grape].possibleFlavors.earthFlavors.map((earthFlavor) => <div onClick={() => updateEarthFlavors(earthFlavor)} className={fruitFlavors[earthFlavor] === earthFlavor ? 'toggled__flavor' : ''}>{earthFlavor}</div>)}</div>

    <div>Other: {wineDB[grape].possibleFlavors.others.map((other) => <div onClick={() => updateOtherFlavors(other)} className={fruitFlavors[other] === other ? 'toggled__flavor' : ''}>{other}</div>)}</div>

    <button onClick={() => console.log(fruitFlavors)}>show fruit flavors</button>
  </div>

  );
}
