import { useState } from "react";

export default function DominantFlavors({
  updateDominantFlavors,
  grape,
  wineDB,
}) {
  const [flavors, setFlavors] = useState({});

  function updateFlavors(flavor) {
    if (flavors[flavor]) {
      delete flavors[flavor];
      setFlavors((prev) => ({
        ...prev,
      }));
    } else {
      setFlavors((prev) => ({
        ...prev,
        [flavor]: flavor,
      }));
    }
  }

  return (
    <div>
      <span className="dominant__flavor__headline">
        <h2>dominant flavors in {grape}</h2>
      </span>

      <div className="dominant__flavors__container">
        <div>
          {wineDB[grape].dominantFlavors.map((flavor) => (
            <div onClick={() => updateFlavors(flavor)}>{flavor}</div>
          ))}
        </div>

        <button
          onClick={() => updateDominantFlavors({ ratingCompleted: true, flavors:flavors })}
        >
          next render
        </button>
      </div>

      <button onClick={() => console.log(flavors)}>flavors</button>
    </div>
  );
}
