import "./App.css";
import React, { useState } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import WineTasting from "./Components/WineTasting/WineTasting";
import WineList from "./Components/WineList/WineList"
import ApiService from './Components/ApiService';

export default function App() {

  const [wineList, setWineList] = useState([])

  function updateWineList(list) {
    setWineList(prev => [...prev, list])
  }

  function getAllTastings () {
    ApiService.getTastings()
  }

  return (
    <Router>
      <div className="grid__container">
        <nav className="navbar sticky">
          <div className='underline__navbar'>
            <Link to="/" className='link__navbar__header' updateWineList={updateWineList}>Virtual Wine Tasting</Link>
          </div>
          <div className='underline__navbar'>
            <Link to="/" className='link__navbar'>Home</Link>
          </div>
          <div className='underline__navbar'>
            <Link to="/tasting" className='link__navbar'>Start Wine Tasting</Link>
          </div>
          <div className='underline__navbar'>
            <Link to="/winelist" className='link__navbar'>Your Personal Wine List</Link>
          </div>
          <div className='underline__navbar'>
            <Link to="/login" className='link__navbar'>login</Link>
          </div>
          <button onClick={() => getAllTastings}>get tastings from db</button>
        </nav>


        <div className="main">
          <Switch>
            <Route path="/tasting">
              <WineTasting updateWineList={updateWineList} />
            </Route>
            <Route path="/winelist"><WineList wineList={wineList} /></Route>
            <Route path="/login">{/* <Users /> */}</Route>
            <Route path="/">{/* <Home /> */}</Route>
          </Switch>
        </div>
      </div>
    </Router>
  );
}
