const axios = require('axios');
const url = 'http://localhost:3001';

// GET ALL WINE TASTINGS FROM DB
async function getTastings() {
  try {
    const response = await axios.get(url + '/api/tastings');
    // console.log(response);
    return response.data;
  } catch (error) {
    console.error(error);
  }
}

// POST ONE TASTING TO DB
function postTasting(options) {
  axios.post(url + '/api/tastings', options)
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
}

//DELETE TASTING BY ID
function deleteTasting(id) {
  axios.delete(url + '/api/tastings/' + id)
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
}


export default { getTastings, postTasting, deleteTasting };


